(()=>{class e{constructor(e,t,d){this.name=e,this.priority=t,this.deadline=d}}class t{static displayTasks(){[{name:"Call Mum",priority:"high",deadline:"01/02/2023"},{name:"Mow lawn",priority:"medium",deadline:"15/02/2023"}].forEach((e=>t.addTaskToList(e)))}static addTaskToList(e){const t=document.getElementById("task-list"),d=document.createElement("tr");d.innerHTML=`\n    <td>${e.name}</td>\n    <td>${e.priority}</td>\n    <td>${e.deadline}</td>\n    <td><a href="#" class="btn btn-danger btn-sm delete">X</a></td>\n    `,t.appendChild(d)}static deleteTask(e){e.classList.contains("delete")&&e.parentElement.parentElement.remove()}static clearFields(){document.getElementById("name").value="",document.getElementById("priority").value="",document.getElementById("deadline").value=""}}document.addEventListener("DOMContentLoaded",t.displayTasks),document.getElementById("task-form").addEventListener("submit",(d=>{d.preventDefault;const n=document.getElementById("name").value,a=document.getElementById("priority").value,l=document.getElementById("deadline").value,s=new e(n,a,l);t.addTaskToList(s),t.clearFields()})),document.getElementById("task-list").addEventListener("click",(e=>{t.deleteTask(e.target)}))})();