(()=>{class t{static displayTasks(){[{name:"Call Mum",priority:"high",deadline:"01/02/2023"},{name:"Mow lawn",priority:"medium",deadline:"15/02/2023"}].forEach((d=>t.addTaskToList(d)))}static addTaskToList(t){const d=document.getElementById("task-list"),a=document.createElement("tr");a.innerHTML=`\n    <td>${t.name}</td>\n    <td>${t.priority}</td>\n    <td>${t.deadline}</td>\n    <td><a href="#" class="btn btn-danger">X</a></td>\n    `,d.appendChild(a)}}document.addEventListener("DOMContentLoaded",t.displayTasks)})();